using System.Collections; using System.Collections.Generic; using UnityEngine;  public class SpawnerSC : MonoBehaviour {  public GameObject cubePrefab; public GameObject spherePrefab; public GameObject capsulePrefab; public GameObject spawner;  //一箇所に一定時間ごとにオブジェクトを出す //オブジェクトの発生場所から少し先に不可視のCollider(IsTrigger)を置く     void OnTriggerExit(Collider col){         if (col.gameObject.tag == "tsumiki"){             int objrd = Random.Range(1,11);             GameObject obj = objrd > 8 ? capsulePrefab : (objrd > 5 ? spherePrefab : cubePrefab);             Instantiate(obj, spawner.transform.position, Quaternion.identity);         }     } } 